$(document).ready(function() {

	function dialog_init() {
		$('#dialog_show_div').hide();
		$('#dialog_bg_div').hide();
	}

	function dialog_show() {
		$('#dialog_show_div').show();
		$('#dialog_bg_div').show();
	}

	function dialog_close() {
		$('#dialog_show_div').hide();
		$('#dialog_bg_div').hide();
	}

	function region_init() {

		$('#region_menu').append('<div id="region_father"><ul></ul></div>');
		$('#region_menu').append('<div id="region_children"><ul></ul></div>');

		for (key in region_json) {

			/* <input /> */
			var node_input = $('<input type="checkbox" />');
			node_input.attr('value', key);

			/* <span><input /></span> */
			var node_span = $('<span></span>');
			node_span.append(node_input);
			node_span.append(region_json[key]);

			/* <li><span><input /></span></li> */
			var node_li = $('<li></li>');
			node_li.append(node_span);

			if (key.substring(5) == '00000') {

				node_li.click(function() {

					var region_id = 'region_' + $(this).children('span').children('input').val();
					$('#region_children div').each(function () {

						if (region_id == $(this).attr('id')) {
							$(this).show();
						} else {
							$(this).hide();
						}
					});
				});

				$('#region_father').children('ul').append(node_li); 

				var node_div = $('<div></div>');
				node_div.attr('id', 'region_' + key.substring(0, 5) + '00000');
				node_div.hide();
				$('#region_children').children('ul').append(node_div);

			} else {

				var id = '#region_' + key.substring(0, 5) + '00000';
				$(id).append(node_li); 
			}
		}
	}

	function disease_init() {

		$('#disease_menu').append('<ul></ul>');

		for (key in disease_json) {

			/* <input /> */
			var node_input = $('<input type="checkbox" />');
			node_input.attr('value', key);

			/* <span><input /></span> */
			var node_span = $('<span></span>');
			node_span.append(disease_json[key]);
			node_span.click(function() {

				if ($(this).next('ul').is(":hidden")) {
					$(this).next('ul').show();
				} else {
					$(this).next('ul').hide();
				}
			});

			/* <div><span><input /></span><ul></ul></div> */
			var node_div = $('<div></div>');
			node_div.attr('id', 'disease_' + key);
			node_div.append(node_input);
			node_div.append(node_span);
			node_div.append('<ul style="display: none;"></ul>');

			/* <ul><div><span><input /></span><ul></ul></div></ul> */
			var node_li = $('<li></li>');
			node_li.append(node_div);

			if (key.substring(4) == '000000') {
				/* root */
				$('#disease_menu').children('ul').append(node_li); // add root

			} else {

				if (key.substring(7) == '000')	{				
					/* level-2 */
					node_div.attr('class', 'disease_level-2');
					var id = '#disease_' + key.substring(0, 4) + '000000';
					$(id).children('ul').append(node_li); // add level-2
					

				} else {
					/* level-3 */
					node_div.attr('class', 'disease_level-3');
					var id = '#disease_' + key.substring(0, 7) + '000';
					$(id).children('ul').append(node_li); // add level-3
				}	
			}
		}

	}

	dialog_init();
	region_init();
	disease_init();

	$('#add_campaign').click(function() {
		$('#region_menu').show();
		dialog_show();
	});

	$('#add_group').click(function() {
		$('#disease_menu').show();
		dialog_show();
	});

	$('#add_creative').click(function() {
		$('#creative_menu').show();
		dialog_show();
	});

	$('#dialog_button_yes').click(function() {
		$('#region_menu').hide();
		$('#disease_menu').hide();
		$('#creative_menu').hide();
		dialog_close();
	});

	$('#dialog_button_no').click(function() {
		$('#region_menu').hide();
		$('#disease_menu').hide();
		$('#creative_menu').hide();
		dialog_close();
	});

});









