	function dialog_region_init() {

		$('#campaign_menu').html("");
		$('#campaign_menu').append('<input type="radio" name="dialog_region_radio" value="1" id="dialog_region_all" checked="checked" />全部地域');
		$('#campaign_menu').append('<input type="radio" name="dialog_region_radio" value="2" id="dialog_region_select" />指定地域');

		$('#campaign_menu').append('<div id="region_father"><ul></ul></div>');
		$('#campaign_menu').append('<div id="region_children"><ul></ul></div>');

		for (key in region_json) {

			/* <input /> */
			var node_input = $('<input type="checkbox" />');
			node_input.click(function(){

				var status = $(this).attr('checked');

				var key = $(this).val();
				if (key.substring(5) == '00000') {

					key = key.substring(0, 5);
					$('#region_children input').each(function () {

						var c_id = $(this).val().substring(0, 5);
						if (key == c_id) {

							if (status) {
								$(this).attr('checked', true);
							} else {

								$(this).attr('checked', false);
							}
						}
					});
				}
			});

			node_input.val(key);

			/* <span><input /></span> */
			var node_span = $('<span></span>');
			node_span.append(node_input);
			node_span.append(region_json[key]);

			/* <li><span><input /></span></li> */
			var node_li = $('<li></li>');
			node_li.append(node_span);

			if (key.substring(5) == '00000') {

				node_li.click(function() {

					var region_id = 'region_' + $(this).children('span').children('input').val();
					$('#region_children div').each(function () {

						if (region_id == $(this).attr('id')) {
							$(this).show();
						} else {
							$(this).hide();
						}
					});
				});

				$('#region_father').children('ul').append(node_li); 

				var node_div = $('<div></div>');
				node_div.attr('id', 'region_' + key.substring(0, 5) + '00000');
				node_div.hide();
				$('#region_children').children('ul').append(node_div);

			} else {

				var id = '#region_' + key.substring(0, 5) + '00000';
				$(id).append(node_li); 
			}
		}
	}

	function dialog_disease_init() {

		$('#group_menu').append('<ul></ul>');

		for (key in disease_json) {

			/* <input /> */
			var node_input = $('<input type="checkbox" />');
			node_input.val(key);

			/* <span><input /></span> */
			var node_span = $('<span></span>');
			node_span.append(disease_json[key]);
			node_span.click(function() {

				if ($(this).next('ul').is(":hidden")) {
					$(this).next('ul').show();
				} else {
					$(this).next('ul').hide();
				}
			});

			/* <div><span><input /></span><ul></ul></div> */
			var node_div = $('<div></div>');
			node_div.attr('id', 'disease_' + key);
			node_div.append(node_input);
			node_div.append(node_span);
			node_div.append('<ul style="display: none;"></ul>');

			/* <ul><div><span><input /></span><ul></ul></div></ul> */
			var node_li = $('<li></li>');
			node_li.append(node_div);

			if (key.substring(4) == '000000') {
				/* root */
				$('#group_menu').children('ul').append(node_li); // add root

			} else {

				if (key.substring(7) == '000')	{				
					/* level-2 */
					node_div.attr('class', 'disease_level-2');
					var id = '#disease_' + key.substring(0, 4) + '000000';
					$(id).children('ul').append(node_li); // add level-2
					

				} else {
					/* level-3 */
					node_div.attr('class', 'disease_level-3');
					var id = '#disease_' + key.substring(0, 7) + '000';
					$(id).children('ul').append(node_li); // add level-3
				}	
			}
		}
	}

	function get_tree() {

		$.post('/action', {

			packet_type:"get_tree",
			ad_type: current_info['ad_type'],
			account_id: current_info["account_id"]
		},

		function(data) {

			var branches = $('#browser');
			branches.html("");

			for (i in data) {

				/* campaign */
				var campaign_id = data[i]["campaign_id"];
				if(!document.getElementById(campaign_id)) {	

					var node_campaign_span_span = $('<span></span>');
					node_campaign_span_span.val(data[i]["campaign_id"]);
					node_campaign_span_span.append(data[i]["campaign_name"]);
					node_campaign_span_span.click(function() {

						var campaign_id = $(this).val();
						current_info['add_button_type'] = 'group';
						current_info['campaign_id'] = campaign_id;
						$('#add_new_data').val('(+)创建组');
						get_group(campaign_id);
					});

					var node_campaign_span = $('<span class="folder"></span>');
					node_campaign_span.append('&nbsp;&nbsp;');
					node_campaign_span.append(node_campaign_span_span);

					var node_campaign = $('<li></li>');
					node_campaign.attr('id', campaign_id);
					node_campaign.attr('name', data[i]["campaign_name"]);
					node_campaign.attr('etype', 'campaign');
					node_campaign.append(node_campaign_span);
					node_campaign.append('<ul></ul>');

					branches.append(node_campaign);
				}

				/* group */
				if (data[i]["group_id"] && data[i]["group_name"]) {

					var node_group_span = $('<span class="file"></span>');
					node_group_span.append(data[i]["group_name"]);
					node_group_span.val(data[i]["group_id"]);
					node_group_span.click(function(){

						var group_id = $(this).val();
						current_info['add_button_type'] = 'creative';
						current_info['group_id'] = group_id;
						$('#add_new_data').val('(+)创建创意');

						get_creative();
					});

					var node_group = $('<li></li>');
					node_group.attr('id', data[i]["group_id"]);
					node_group.attr('name', data[i]["group_name"]);
					node_group.append(node_group_span);

					$('#'+campaign_id).children('ul').append(node_group);
				}
			}

			branches.treeview({add: branches});
		});  // post end
	}

	function add_campaign() {

		var campaign_name = $('#dialog_name').val();
		var region_type = $('input[name="dialog_region_radio"]:checked').val();

		var region_code = new Array();
		$('#region_father input:checked').each(function () {
			region_code.push($(this).val());
		});

		$('#region_children input:checked').each(function () {
			region_code.push($(this).val());
		});

		$.post('/action', {

			packet_type: "add_campaign",
			ad_type: current_info['ad_type'],
			account_id: current_info["account_id"],
			campaign_name: campaign_name,
			region_type: region_type,
			region_code: region_code
		});

		get_tree();
		get_campaign();
	}

	function get_campaign_one(campaign_id) {

		$.post('/action', {

			packet_type:"get_campaign_one",
			ad_type: current_info['ad_type'],
			account_id: current_info["account_id"],
			campaign_id: campaign_id
		},

		function(data) {

			var campaign_name = data['campaign_name'];
			$('#dialog_name').val(campaign_name);

			var region_type = data['region_type'];
			if (region_type == '1') {
				$('#dialog_region_all').attr('checked', true);
			} else {
				$('#dialog_region_select').attr('checked', true);
			}

			var region_code = data['region_code'].split(',');
			$('#region_father input').each(function () {

				$(this).attr('checked', false);

				var code = $(this).val();
				for (key in region_code) {
					if (code == region_code[key]) {
						$(this).attr('checked', true);
					}
				}
			});

			$('#region_children input').each(function () {

				$(this).attr('checked', false);

				var code = $(this).val();
				for (key in region_code) {
					if (code == region_code[key]) {
						$(this).attr('checked', true);
					}
				}
			});			
		});
	}

