	function dialog_creative_show(img_id) {

		var ad_type = current_info['ad_type'];
		if (ad_type == 1) {
			$('#creative_txt_menu').show();
			dialog_show();

		} else if (ad_type == 2) {
			$('#creative_img_menu').show();

			if (current_info['dialog_type'] == 'add_creative') {
				
				$('#creative_img_upload').show();
				dialog_creative_img();

			} else 	if (current_info['dialog_type'] == 'set_creative') {

				$('#creative_img_upload').hide();
				$('#creative_img_upload_list').html("");

				var path = '/images/' + img_id[0] + '/' + img_id[1] + '/' + img_id[2] + '/' + img_id[3] + '/' + img_id + '.jpg';
				$('#creative_img_upload_list').append('<img src=' + path + '></img>');
			}

		} else if (ad_type == 3) {
			$('#creative_brand_menu').show();
			dialog_show();
		}

		dialog_show();
	}

	function dialog_creative_img() {

		current_info['dialog_type'] = 'add_creative';
		dialog_show();

		var upload = $('#creative_img_upload').upload();

		/* 显示上传列表 */
		$('#creative_img_upload_list').html("");
		upload.on('fileQueued', function( file ) {

			$('#creative_img_upload_list').append('<div>'+file['name']+'</div>');
		});

		/* 上传成功后触发事件 */
		upload.on('uploadSuccess',function(file, response) {

			var group_id = current_info['group_id'];
			var creative_name = file['name'];
			var access_url =  $('#creative_img_access_url').val();
			var display_url =  $('#creative_img_display_url').val();
			var other = $('#creative_img_other').val();


			var res = jQuery.parseJSON(response['_raw']);
			var img_id = res['img_id'];
			var img_size = res['img_size'];

			alert(img_id);
			alert(img_size);
			alert(res);

			$.post('/action', {

				packet_type: "add_creative",
				ad_type: current_info['ad_type'],
				account_id: current_info["account_id"],
				group_id: group_id,
				creative_name: creative_name,
				img_id: img_id,
				img_size: img_size,
				access_url: access_url,
				display_url: display_url,
				other:other
			});

			get_creative();
		});

		/* 绑定发送按钮 */
		$('#dialog_button_yes').click(function(){

			upload.upload();
		});
	}

