	$('#__find_tree').keyup(function() {

		var key = $(this).val();


		$('#browser li').each(function() {

			$(this).show();

			var type = $(this).attr('etype');
			var name = $(this).attr('name');

			if (type == 'campaign') {

				if (name.indexOf(key) < 0) {

					$(this).hide();
				}
			}
		});

		$('#browser li').each(function() {

			var type = $(this).attr('etype');
			var name = $(this).attr('name');

			if (type != 'campaign') {

				if (name.indexOf(key) >= 0) {

					$(this).parent().parent().show();
				}
			}
		});

	});





